// src/components/application/CombinedForm.tsx
import React, { useState } from 'react';
import { FormData } from '../../types';
import './CombinedForm.css';

interface CombinedFormProps {
  setFormData: React.Dispatch<React.SetStateAction<FormData>>;
  nextStep: () => void;
}

const CombinedForm: React.FC<CombinedFormProps> = ({ setFormData, nextStep }) => {
  const [localData, setLocalData] = useState<FormData>({
    school: '',
    major: '',
    examCombination: '',
    personalInfo: { name: '', dateOfBirth: '', address: '', phone: '', cccd: '', ethnicity: '' },
    scores: {},
    priorityCategories: [],
    documents: []
  });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setLocalData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePersonalInfoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setLocalData((prev) => ({
      ...prev,
      personalInfo: { ...prev.personalInfo, [name]: value }
    }));
  };

  const handleScoreChange = (subject: string, value: string) => {
    setLocalData((prev) => ({
      ...prev,
      scores: { ...prev.scores, [subject]: parseFloat(value) }
    }));
  };

  const handlePriorityChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setLocalData((prev) => ({
      ...prev,
      priorityCategories: [e.target.value]
    }));
  };

  const handleSubmit = () => {
    setFormData(localData);
    nextStep();
  };

  return (
    <div className="combined-form">
      <h2>Đăng ký xét tuyển</h2>

    {/* Thông tin cá nhân */}
<div className="form-section personal-info">
  <h3>Thông tin cá nhân</h3>
  <div className="personal-grid">
    <div className="field">
      <label>Họ và tên đệm:</label>
      <input
        type="text"
        name="fullName"
        placeholder="HỌ VÀ TÊN ĐỆM"
        value={localData.personalInfo.fullName}
        onChange={handlePersonalInfoChange}
      />
    </div>
    <div className="field">
      <label>Tên:</label>
      <input
        type="text"
        name="name"
        placeholder="TÊN"
        value={localData.personalInfo.name}
        onChange={handlePersonalInfoChange}
      />
    </div>
    <div className="field">
      <label>Giới tính:</label>
      <select
        name="gender"
        value={localData.personalInfo.gender}
        onChange={handlePersonalInfoChange}
      >
        <option value="Nam">Nam</option>
        <option value="Nữ">Nữ</option>
      </select>
    </div>
    <div className="photo-upload">
      <div className="avatar-placeholder">
        {/* bạn có thể hiển thị ảnh preview ở đây */}
        <span>Ảnh đại diện</span>
      </div>
      <button type="button" onClick={handleUploadClick}>
        Chọn ảnh
      </button>
      <input
        type="file"
        accept="image/*"
        hidden
        ref={fileInputRef}
        onChange={handleFileChange}
      />
    </div>

    <div className="field">
      <label>Ngày sinh:</label>
      <input
        type="date"
        name="dateOfBirth"
        value={localData.personalInfo.dateOfBirth}
        onChange={handlePersonalInfoChange}
      />
    </div>
    <div className="field">
      <label>Nơi sinh:</label>
      <select
        name="placeOfBirth"
        value={localData.personalInfo.placeOfBirth}
        onChange={handlePersonalInfoChange}
      >
        <option value="">Chọn nơi sinh</option>
        <option value="HaNoi">Hà Nội</option>
        {/* ... */}
      </select>
    </div>
    <div className="field">
      <label>Dân tộc:</label>
      <select
        name="ethnicity"
        value={localData.personalInfo.ethnicity}
        onChange={handlePersonalInfoChange}
      >
        <option value="Kinh">Kinh</option>
        {/* ... */}
      </select>
    </div>
    <div className="field">
      <label>Quốc tịch:</label>
      <input
        type="checkbox"
        name="foreignNationality"
        checked={localData.personalInfo.foreignNationality}
        onChange={handleCheckboxChange}
      />
    </div>

    <div className="field full-width">
      <label>Số CCCD/CMND:</label>
      <input
        type="text"
        name="cccd"
        value={localData.personalInfo.cccd}
        onChange={handleNumericChange}
      />
    </div>

    <div className="field full-width">
      <label>Nơi thường trú:</label>
      <div className="address-grid">
        <input name="province" placeholder="Tỉnh/TP" />
        <input name="district" placeholder="Huyện/Quận" />
        <input name="ward" placeholder="Xã/Phường" />
      </div>
    </div>
  </div>
</div>

    </div>
  );
};

export default CombinedForm;
