import React from 'react';
import { FormData } from '../../types';
import CommonLayout from './CommonLayout';
import '../../styles.css';
import '../../styles/Up';

interface Step4Props {
  formData: FormData;
  setFormData: React.Dispatch<React.SetStateAction<FormData>>;
  nextStep: () => void;
  prevStep: () => void;
  setPage: (page: string) => void;
  currentUser?: string | null | undefined;
}

const Step4_UploadDocuments: React.FC<Step4Props> = ({ 
  formData, 
  setFormData, 
  nextStep, 
  prevStep,
  setPage,
  currentUser
}) => {
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      const newDocuments = Array.from(e.target.files);
      setFormData(prev => ({ ...prev, documents: newDocuments }));
    }
  };

  return (
    <CommonLayout setPage={setPage} currentUser={currentUser} activePage="apply">
      <div className="form-step">
        <h2>Tải lên minh chứng</h2>
        <div className="form-group">
          <label>Chọn tệp (PDF, JPEG, PNG)</label>
          <div className="file-input-container">
            <input
              type="file"
              accept=".pdf,.jpeg,.jpg,.png"
              multiple
              onChange={handleFileChange}
              className="file-input"
              id="document-upload"
            />
            <label htmlFor="document-upload" className="file-input-label">
              Chọn tệp...
            </label>
          </div>
        </div>
        {formData.documents.length > 0 && (
          <div className="file-list-container">
            <h3>Các tệp đã chọn:</h3>
            <ul className="file-list">
              {formData.documents.map((file: File, index: number) => (
                <li key={index} className="file-list-item">
                  <span className="file-name">{file.name}</span>
                  <span className="file-size">{(file.size / 1024).toFixed(2)} KB</span>
                </li>
              ))}
            </ul>
          </div>
        )}
        <div className="form-navigation">
          <button 
            className="prev-button" 
            onClick={prevStep}
          >
            Quay lại
          </button>
          <button 
            className="next-button" 
            onClick={nextStep}
            disabled={formData.documents.length === 0}
          >
            Tiếp theo
          </button>
        </div>
      </div>
    </CommonLayout>
  );
};

export default Step4_UploadDocuments;
