import React, { useState } from 'react';
import { FormData } from '../../types';
import CombinedForm from './CombinedForm';
import Step4_UploadDocuments from './Step4_UploadDocuments';
import Step5_ReviewAndSubmit from './Step5_ReviewAndSubmit';
import '../../styles.css';

// Define possible page types for type safety
type Page = 'dashboard' | 'status' | 'results' | 'apply';

interface ApplicationFormProps {
  onNavigate: (page: Page) => void;
}

const ApplicationForm: React.FC<ApplicationFormProps> = ({ onNavigate }) => {
  const [step, setStep] = useState<1 | 2 | 3>(1);
  const [formData, setFormData] = useState<FormData>({
    school: '',
    major: '',
    examCombination: '',
    personalInfo: { 
      name: '', 
      dateOfBirth: '', 
      address: '', 
      phone: '', 
      cccd: '', 
      ethnicity: '', 
      gender: '' 
    },
    scores: {},
    priorityCategories: [],
    documents: []
  });

  const nextStep = () => {
    if (step < 3) {
      setStep((step + 1) as 1 | 2 | 3);
    }
  };

  const prevStep = () => {
    if (step > 1) {
      setStep((step - 1) as 1 | 2 | 3);
    } else {
      onNavigate('dashboard'); // Return to dashboard if on first step
    }
  };

  const handleFormSubmit = async () => {
    try {
      console.log('Đang gửi hồ sơ:', formData);
      // TODO: Replace with actual API call
      // await api.submitApplication(formData);
      console.log('Hồ sơ đã được gửi thành công');
      onNavigate('status');
    } catch (error) {
      console.error('Lỗi khi gửi hồ sơ:', error);
      // TODO: Add user notification
    }
  };

  const renderStep = () => {
    switch (step) {
      case 1:
        return (
          <CombinedForm 
            formData={formData}
            setFormData={setFormData} 
            onNext={nextStep} 
            onCancel={() => onNavigate('dashboard')}
          />
        );
      case 2:
        return (
          <Step4_UploadDocuments
            formData={formData}
            setFormData={setFormData}
            onNext={nextStep}
            onBack={prevStep}
          />
        );
      case 3:
        return (
          <Step5_ReviewAndSubmit
            formData={formData}
            onBack={prevStep}
            onSubmit={handleFormSubmit}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className="application-form">
      <div className="form-header">
        <h2>Đăng ký xét tuyển đại học</h2>
        <div className="step-indicator">
          Bước {step}/3: {['Thông tin cơ bản', 'Tải lên tài liệu', 'Xem lại và gửi'][step - 1]}
        </div>
      </div>
      {renderStep()}
    </div>
  );
};

export default ApplicationForm;
