import React, { useState } from 'react';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import Dashboard from './pages/Dashboard';
import ApplicationForm from './components/application/ApplicationForm';
import ApplicationStatus from './components/application/ApplicationStatus';
import Results from './components/application/Results';
import './styles.css';

const App: React.FC = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState<string | undefined>(undefined);
  const [currentPage, setCurrentPage] = useState('dashboard');

  const handleLogin = (username: string) => {
    setLoggedIn(true);
    setCurrentUser(username);
    setCurrentPage('dashboard');
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setCurrentUser(undefined);
    setCurrentPage('dashboard');
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'login':
        return <Login 
                onLogin={handleLogin} 
                onNavigate={() => setCurrentPage('register')} 
              />;
      
      case 'register':
        return <Register 
                onRegister={handleLogin} 
                onNavigate={() => setCurrentPage('login')} 
              />;
      
      case 'dashboard':
        return <Dashboard 
                currentUser={currentUser} 
                onNavigate={setCurrentPage} 
                onLogout={handleLogout}
              />;
      
      case 'apply':
        if (!loggedIn) {
          setCurrentPage('dashboard');
          return null;
        }
        return <ApplicationForm onNavigate={setCurrentPage} />;
      
      case 'status':
        if (!loggedIn) {
          setCurrentPage('dashboard');
          return null;
        }
        return <ApplicationStatus onNavigate={setCurrentPage} />;
      
      case 'results':
        if (!loggedIn) {
          setCurrentPage('dashboard');
          return null;
        }
        return <Results onNavigate={setCurrentPage} />;
      
      case 'logout':
        handleLogout();
        return null;
      
      default:
        return <Dashboard 
                currentUser={currentUser} 
                onNavigate={setCurrentPage} 
                onLogout={handleLogout}
              />;
    }
  };

  return renderPage();
};

export default App;
