import React from 'react';
import { FormData } from '../../types';
import CommonLayout from './CommonLayout';
import '../../styles.css';
import '../';

interface Step4Props {
  formData: FormData;
  setFormData: React.Dispatch<React.SetStateAction<FormData>>;
  nextStep: () => void;
  prevStep: () => void;
  setPage: (page: string) => void;
  currentUser?: string | null | undefined;
}

type DocumentType = 'reportCard' | 'idCard' | 'portrait';

const Step4_UploadDocuments: React.FC<Step4Props> = ({ 
  formData, 
  setFormData, 
  nextStep, 
  prevStep,
  setPage,
  currentUser
}) => {
  const handleFileChange = (type: DocumentType, e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setFormData(prev => ({ 
        ...prev, 
        documents: {
          ...prev.documents,
          [type]: file
        } 
      }));
    }
  };

  const documentTypes: { 
    type: DocumentType; 
    label: string; 
    accept: string 
  }[] = [
    { 
      type: 'reportCard', 
      label: 'Ảnh chụp học bạ', 
      accept: 'image/*,.pdf' 
    },
    { 
      type: 'idCard', 
      label: 'Ảnh chụp CCCD/CMND', 
      accept: 'image/*,.pdf' 
    },
    { 
      type: 'portrait', 
      label: 'Ảnh chân dung (3x4)', 
      accept: 'image/*' 
    },
  ];

  return (
    <CommonLayout setPage={setPage} currentUser={currentUser} activePage="apply">
      <div className="form-step">
        <h2>Tải lên minh chứng</h2>
        
        {documentTypes.map((docType) => (
          <div className="form-group" key={docType.type}>
            <label>{docType.label}</label>
            <div className="file-input-container">
              <input
                type="file"
                accept={docType.accept}
                onChange={(e) => handleFileChange(docType.type, e)}
                className="file-input"
                id={`${docType.type}-upload`}
              />
              <label 
                htmlFor={`${docType.type}-upload`} 
                className="file-input-label"
              >
                {formData.documents[docType.type] ? 'Thay đổi tệp' : 'Chọn tệp'}
              </label>
              {formData.documents[docType.type] && (
                <div className="file-info">
                  <span className="file-name">
                    {formData.documents[docType.type]?.name}
                  </span>
                  <span className="file-size">
                    {((formData.documents[docType.type]?.size || 0) / 1024).toFixed(2)} KB
                  </span>
                </div>
              )}
            </div>
          </div>
        ))}

        <div className="form-navigation">
          <button 
            className="prev-button" 
            onClick={prevStep}
          >
            Quay lại
          </button>
          <button 
            className="next-button" 
            onClick={nextStep}
            disabled={
              !formData.documents.reportCard || 
              !formData.documents.idCard || 
              !formData.documents.portrait
            }
          >
            Tiếp theo
          </button>
        </div>
      </div>
    </CommonLayout>
  );
};

export default Step4_UploadDocuments;
