// src/components/application/CombinedForm.tsx
import React, { useState } from 'react';
import { FormData } from '../../types';
import './CombinedForm.css';

interface CombinedFormProps {
  setFormData: React.Dispatch<React.SetStateAction<FormData>>;
  nextStep: () => void;
}

const CombinedForm: React.FC<CombinedFormProps> = ({ setFormData, nextStep }) => {
  const [localData, setLocalData] = useState<FormData>({
    school: '',
    major: '',
    examCombination: '',
    personalInfo: { name: '', middleName: '', dateOfBirth: '', placeOfBirth: '', ethnicity: '', foreignNationality: false, cccd: '', addressProvince: '', addressDistrict: '', addressWard: '' },
    scores: {},
    priorityCategories: [],
    documents: []
  });

  const handlePersonalChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value, type, checked } = e.target;
    setLocalData(prev => ({
      ...prev,
      personalInfo: {
        ...prev.personalInfo,
        [name]: type === 'checkbox' ? checked : value
      }
    }));
  };

  const handleSubmit = () => {
    setFormData(localData);
    nextStep();
  };

  return (
    <div className="combined-form">
      <h2>Đăng ký xét tuyển</h2>

      {/* Thông tin cá nhân */}
      <div className="form-section personal-info">
        {/* 1. Họ chữ đệm, Tên, Giới tính */}
        <div className="row three-cols">
          <div className="field">
            <label>1. Họ chữ đệm và tên: <span className="required">*</span></label>
            <input type="text" name="middleName" placeholder="NGUYỄN VĂN" value={localData.personalInfo.middleName} onChange={handlePersonalChange} />
          </div>
          <div className="field">
            <label>&nbsp;</label>
            <input type="text" name="name" placeholder="AN" value={localData.personalInfo.name} onChange={handlePersonalChange} />
          </div>
          <div className="field">
            <label>Giới tính:</label>
            <select name="foreignNationality" value={localData.personalInfo.foreignNationality ? 'on' : 'off'} onChange={handlePersonalChange}>
              <option value="on">Nam</option>
              <option value="off">Nữ</option>
            </select>
          </div>
        </div>
        {/* 2. Ngày tháng năm sinh */}
        <div className="row">
          <label>2. Ngày tháng năm sinh: <span className="required">*</span></label>
          <input type="date" name="dateOfBirth" value={localData.personalInfo.dateOfBirth} onChange={handlePersonalChange} />
          <small>(dd/mm/yy)</small>
        </div>
        {/* 3a. Nơi sinh */}
        <div className="row two-cols">
          <div className="field">
            <label>3a) Nơi sinh: <span className="required">*</span></label>
            <select name="placeOfBirth" value={localData.personalInfo.placeOfBirth} onChange={handlePersonalChange}>
              <option value="">Chọn tỉnh/TP</option>
              <option value="HaNoi">Hà Nội</option>
            </select>
          </div>
          <div className="field">
            <label>&nbsp;</label>
            <input type="text" disabled placeholder="" />
          </div>
        </div>
        {/* 3b. Dân tộc */}
        <div className="row two-cols">
          <div className="field">
            <label>3b) Dân tộc: <span className="required">*</span></label>
            <select name="ethnicity" value={localData.personalInfo.ethnicity} onChange={handlePersonalChange}>
              <option value="">Chọn dân tộc</option>
              <option value="Kinh">Kinh</option>
            </select>
          </div>
          <div className="field">
            <label>&nbsp;</label>
            <input type="text" disabled placeholder="" />
          </div>
        </div>
        {/* 3c. Quốc tịch nước ngoài */}
        <div className="row">
          <label>3c) Quốc tịch nước ngoài:</label>
          <input type="checkbox" name="foreignNationality" checked={localData.personalInfo.foreignNationality} onChange={handlePersonalChange} />
        </div>
        {/* 4. CCCD/CMND */}
        <div className="row">
          <label>4. Số CCCD/CMND: <span className="required">*</span></label>
          <input type="text" name="cccd" value={localData.personalInfo.cccd} onChange={handlePersonalChange} />
        </div>
        {/* 5. Nơi thường trú */}
        <div className="row address-grid">
          <label>5. Nơi thường trú: <span className="required">*</span></label>
          <input name="addressProvince" placeholder="Mã Tỉnh/TP" value={localData.personalInfo.addressProvince} onChange={handlePersonalChange} />
          <input name="addressDistrict" placeholder="Mã Huyện/Quận" value={localData.personalInfo.addressDistrict} onChange={handlePersonalChange} />
          <input name="addressWard" placeholder="Mã Xã/Phường" value={localData.personalInfo.addressWard} onChange={handlePersonalChange} />
        </div>
      </div>

      {/* Các section khác giữ nguyên cấu trúc */}
      {/* ... */}

      <button className="next-button" onClick={handleSubmit}>Tiếp theo</button>
    </div>
  );
};

export default CombinedForm;
