import React, { useState } from "react";
import { FormData } from "../types";
import "../styles.css";

interface AdminDashboardProps {
  setPage: (page: string) => void;
  currentUser?: string | null;
  applications: FormData[];
  onUpdateStatus: (applicationId: number, newStatus: string) => void;
}

interface University {
  id: number;
  name: string;
  code: string;
  majors: string[];
}

interface ExamCombination {
  id: number;
  code: string;
  subjects: string[];
  description: string;
}

const AdminDashboard: React.FC<AdminDashboardProps> = ({
  setPage,
  currentUser,
  applications,
  onUpdateStatus,
}) => {
  const [activeTab, setActiveTab] = useState<"applications" | "edit">(
    "applications"
  );
  const [editTab, setEditTab] = useState<"universities" | "combinations">(
    "universities"
  );
  const [showModal, setShowModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [editingItem, setEditingItem] = useState<any>(null);

  // Mock data - trong thực tế sẽ lấy từ API/database
  const [universities, setUniversities] = useState<University[]>([
    {
      id: 1,
      name: "Đại học Bách Khoa Hà Nội",
      code: "BKA",
      majors: ["Công nghệ thông tin", "Điện tử"],
    },
    {
      id: 2,
      name: "Đại học Công nghệ - ĐHQGHN",
      code: "QHI",
      majors: ["Công nghệ thông tin", "Khoa học máy tính"],
    },
  ]);

  const [examCombinations, setExamCombinations] = useState<ExamCombination[]>([
    {
      id: 1,
      code: "A00",
      subjects: ["Toán", "Lý", "Hóa"],
      description: "Khối A",
    },
    {
      id: 2,
      code: "A01",
      subjects: ["Toán", "Lý", "Anh"],
      description: "Khối A1",
    },
  ]);

  if (currentUser !== "admin") {
    return (
      <div className="unauthorized">
        <h2>Không có quyền truy cập</h2>
        <button onClick={() => setPage("dashboard")} className="back-button">
          Quay lại
        </button>
      </div>
    );
  }

  const handleAdd = () => {
    setEditingItem(null);
    setShowModal(true);
  };

  const handleEdit = (item: any) => {
    setEditingItem(item);
    setShowModal(true);
  };

  const handleDelete = (id: number) => {
    if (editTab === "universities") {
      setUniversities(universities.filter((uni) => uni.id !== id));
    } else {
      setExamCombinations(examCombinations.filter((combo) => combo.id !== id));
    }
  };

  const handleSave = (formData: any) => {
    if (editTab === "universities") {
      if (editingItem) {
        setUniversities(
          universities.map((uni) =>
            uni.id === editingItem.id ? { ...formData, id: uni.id } : uni
          )
        );
      } else {
        setUniversities([
          ...universities,
          { ...formData, id: universities.length + 1 },
        ]);
      }
    } else {
      if (editingItem) {
        setExamCombinations(
          examCombinations.map((combo) =>
            combo.id === editingItem.id ? { ...formData, id: combo.id } : combo
          )
        );
      } else {
        setExamCombinations([
          ...examCombinations,
          { ...formData, id: examCombinations.length + 1 },
        ]);
      }
    }
    setShowModal(false);
  };

  const renderModal = () => (
    <div className="admin-modal">
      <div className="modal-content">
        <div className="modal-header">
          <h4>{editingItem ? "Chỉnh sửa" : "Thêm mới"}</h4>
          <button className="close-button" onClick={() => setShowModal(false)}>
            &times;
          </button>
        </div>
        <div className="modal-form">
          {editTab === "universities" ? (
            <>
              <input
                type="text"
                placeholder="Tên trường"
                defaultValue={editingItem?.name || ""}
              />
              <input
                type="text"
                placeholder="Mã trường"
                defaultValue={editingItem?.code || ""}
              />
              <input
                type="text"
                placeholder="Các ngành (phân cách bằng dấu phẩy)"
                defaultValue={editingItem?.majors.join(", ") || ""}
              />
            </>
          ) : (
            <>
              <input
                type="text"
                placeholder="Mã tổ hợp"
                defaultValue={editingItem?.code || ""}
              />
              <input
                type="text"
                placeholder="Các môn (phân cách bằng dấu phẩy)"
                defaultValue={editingItem?.subjects.join(", ") || ""}
              />
              <input
                type="text"
                placeholder="Mô tả"
                defaultValue={editingItem?.description || ""}
              />
            </>
          )}
          <div className="modal-buttons">
            <button
              className="cancel-button"
              onClick={() => setShowModal(false)}
            >
              Hủy
            </button>
            <button className="save-button" onClick={() => handleSave({})}>
              Lưu
            </button>
          </div>
        </div>
      </div>
    </div>
  );

  const renderEditContent = () => {
    const filteredData =
      editTab === "universities"
        ? universities.filter(
            (uni) =>
              uni.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
              uni.code.toLowerCase().includes(searchTerm.toLowerCase())
          )
        : examCombinations.filter(
            (combo) =>
              combo.code.toLowerCase().includes(searchTerm.toLowerCase()) ||
              combo.description.toLowerCase().includes(searchTerm.toLowerCase())
          );

    return (
      <div className="admin-edit-section">
        <div className="tab-buttons">
          <button
            className={`tab-button ${editTab === "universities" ? "active" : ""}`}
            onClick={() => setEditTab("universities")}
          >
            Trường đại học
          </button>
          <button
            className={`tab-button ${editTab === "combinations" ? "active" : ""}`}
            onClick={() => setEditTab("combinations")}
          >
            Tổ hợp xét tuyển
          </button>
        </div>

        <div className="admin-controls">
          <input
            type="text"
            className="admin-search"
            placeholder="Tìm kiếm..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button className="admin-add-button" onClick={handleAdd}>
            Thêm mới
          </button>
        </div>

        <div className="admin-table-container">
          <table className="admin-table">
            <thead>
              <tr>
                {editTab === "universities" ? (
                  <>
                    <th>Mã trường</th>
                    <th>Tên trường</th>
                    <th>Các ngành</th>
                    <th>Thao tác</th>
                  </>
                ) : (
                  <>
                    <th>Mã tổ hợp</th>
                    <th>Các môn</th>
                    <th>Mô tả</th>
                    <th>Thao tác</th>
                  </>
                )}
              </tr>
            </thead>
            <tbody>
              {editTab === "universities"
                ? // Render universities
                  (filteredData as University[]).map((item) => (
                    <tr key={item.id}>
                      <td>{item.code}</td>
                      <td>{item.name}</td>
                      <td>{item.majors.join(", ")}</td>
                      <td>
                        <div className="action-buttons">
                          <button
                            className="edit-button"
                            onClick={() => handleEdit(item)}
                          >
                            Sửa
                          </button>
                          <button
                            className="delete-button"
                            onClick={() => handleDelete(item.id)}
                          >
                            Xóa
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                : // Render exam combinations
                  (filteredData as ExamCombination[]).map((item) => (
                    <tr key={item.id}>
                      <td>{item.code}</td>
                      <td>{item.subjects.join(", ")}</td>
                      <td>{item.description}</td>
                      <td>
                        <div className="action-buttons">
                          <button
                            className="edit-button"
                            onClick={() => handleEdit(item)}
                          >
                            Sửa
                          </button>
                          <button
                            className="delete-button"
                            onClick={() => handleDelete(item.id)}
                          >
                            Xóa
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const renderApplicationsTab = () => (
    <div className="applications-list">
      {applications.length === 0 ? (
        <div className="no-applications">
          <p>Chưa có hồ sơ xét tuyển nào.</p>
        </div>
      ) : (
        applications.map((application, index) => (
          <div key={index} className="admin-application-card">
            <div className="application-header">
              <h3>Hồ sơ #{index + 1}</h3>
              <div className="status-controls">
                <select
                  value={application.status}
                  onChange={(e) => onUpdateStatus(index, e.target.value)}
                  className="status-select"
                >
                  <option value="Chờ duyệt">Chờ duyệt</option>
                  <option value="Đã duyệt">Duyệt</option>
                  <option value="Từ chối">Từ chối</option>
                </select>
              </div>
            </div>

            <div className="application-details">
              <div className="detail-section">
                <h4>Thông tin cá nhân</h4>
                <div className="info-grid">
                  <p>
                    <strong>Họ tên:</strong> {application.personalInfo.name}
                  </p>
                  <p>
                    <strong>CCCD:</strong> {application.personalInfo.cccd}
                  </p>
                  <p>
                    <strong>Ngày sinh:</strong>{" "}
                    {application.personalInfo.dateOfBirth}
                  </p>
                  <p>
                    <strong>Giới tính:</strong>{" "}
                    {application.personalInfo.gender}
                  </p>
                </div>
              </div>

              <div className="detail-section">
                <h4>Thông tin đăng ký</h4>
                <div className="info-grid">
                  <p>
                    <strong>Trường:</strong> {application.school}
                  </p>
                  <p>
                    <strong>Ngành:</strong> {application.major}
                  </p>
                  <p>
                    <strong>Tổ hợp xét tuyển:</strong>{" "}
                    {application.examCombination}
                  </p>
                </div>
              </div>

              <div className="detail-section">
                <h4>Điểm xét tuyển</h4>
                <div className="info-grid">
                  {application.transcriptScores ? (
                    <>
                      <p>
                        <strong>Học kỳ 1 - Lớp 12:</strong>
                      </p>
                      {Object.entries(
                        application.transcriptScores.semester1Grade12
                      ).map(([subject, score]) => (
                        <p key={subject}>
                          <strong>{subject}:</strong> {score}
                        </p>
                      ))}
                    </>
                  ) : (
                    Object.entries(application.scores).map(
                      ([subject, score]) => (
                        <p key={subject}>
                          <strong>{subject}:</strong> {score}
                        </p>
                      )
                    )
                  )}
                </div>
              </div>

              <div className="submission-info">
                <p>
                  <strong>Ngày nộp:</strong> {application.submissionDate}
                </p>
              </div>
            </div>
          </div>
        ))
      )}
    </div>
  );

  return (
    <div className="admin-dashboard">
      <div className="admin-header">
        <h2>Quản lý hồ sơ xét tuyển</h2>
        <button onClick={() => setPage("logout")} className="logout-button">
          Đăng xuất
        </button>
      </div>

      <div className="admin-menu">
        <div className="admin-menu-container">
          <div
            className={`admin-menu-item ${activeTab === "applications" ? "active" : ""}`}
            onClick={() => setActiveTab("applications")}
          >
            Duyệt đơn
          </div>
          <div
            className={`admin-menu-item ${activeTab === "edit" ? "active" : ""}`}
            onClick={() => setActiveTab("edit")}
          >
            Chỉnh sửa
          </div>
        </div>
      </div>

      <div className="admin-content">
        {activeTab === "applications"
          ? renderApplicationsTab()
          : renderEditContent()}
      </div>

      {showModal && renderModal()}
    </div>
  );
};

export default AdminDashboard;
