import React, { useState, useEffect } from "react";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import Dashboard from "./pages/Dashboard";
import ApplicationForm from "./components/application/ApplicationForm";
import ApplicationStatus from "./components/application/ApplicationStatus";
import Results from "./pages/Results";
import ExamResults from "./pages/ExamResults";
import GuideRegistration from "./components/application/GuideRegistration";
import TranscriptForm from "./components/application/TranscriptForm";
import AdminDashboard from "./pages/AdminDashboard";
import { UniversityProvider } from "./contexts/UniversityContext";
import { FormData, FormDataSetter } from "./types";
import "./styles.css";

const defaultFormData: FormData = {
  school: "",
  major: "",
  examCombination: "",
  personalInfo: {
    name: "",
    dateOfBirth: "",
    address: "",
    phone: "",
    cccd: "",
    ethnicity: "",
    gender: "",
  },
  scores: {},
  transcriptScores: {
    semester1Grade12: {
      math: "",
      literature: "",
      english: "",
      physics: "",
      chemistry: "",
      biology: "",
    },
    semester2Grade12: {
      math: "",
      literature: "",
      english: "",
      physics: "",
      chemistry: "",
      biology: "",
    },
    semester1Grade11: {
      math: "",
      literature: "",
      english: "",
      physics: "",
      chemistry: "",
      biology: "",
    },
    semester2Grade11: {
      math: "",
      literature: "",
      english: "",
      physics: "",
      chemistry: "",
      biology: "",
    },
  },
  priorityCategories: [],
  documents: [],
  documents2: [],
  documents3: [],
  status: "Chờ duyệt",
  submissionDate: new Date().toLocaleDateString(),
};

const App: React.FC = () => {
  const [currentUser, setCurrentUser] = useState<string | null>(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [page, setPage] = useState("dashboard");
  const [formData, setFormData] = useState<FormData | null>(null);
  const [allApplications, setAllApplications] = useState<FormData[]>(() => {
    const savedApplications = localStorage.getItem("applications");
    return savedApplications ? JSON.parse(savedApplications) : [];
  });

  useEffect(() => {
    const user = localStorage.getItem("currentUser");
    if (user) {
      setCurrentUser(user);
      setIsAdmin(user === "admin");
    }
  }, []);

  useEffect(() => {
    if (page === "logout") {
      setCurrentUser(null);
      setIsAdmin(false);
      localStorage.removeItem("currentUser");
      setPage("login");
    }
  }, [page]);

  // Lưu applications vào localStorage mỗi khi có thay đổi
  useEffect(() => {
    localStorage.setItem("applications", JSON.stringify(allApplications));
  }, [allApplications]);

  // Add debug logging for initial state
  useEffect(() => {
    console.log("Initial state loaded:");
    console.log("Current user:", currentUser);
    console.log(
      "All applications from localStorage:",
      JSON.parse(localStorage.getItem("applications") || "[]")
    );
  }, []);

  const handleNextStep = () => {
    if (formData && currentUser) {
      console.log("Starting form submission process...");
      console.log("Current form data:", formData);
      console.log("Current applications in state:", allApplications);

      const updatedFormData = {
        ...formData,
        status: "Chờ duyệt",
        submissionDate: new Date().toLocaleDateString(),
        personalInfo: {
          ...formData.personalInfo,
          name: currentUser,
        },
      };

      console.log("Prepared updated form data:", updatedFormData);

      try {
        // First update state
        setAllApplications((prevApplications) => {
          console.log("Previous applications:", prevApplications);
          const newApplications = [...prevApplications, updatedFormData];
          console.log("New applications array:", newApplications);

          // Then update localStorage
          try {
            localStorage.setItem(
              "applications",
              JSON.stringify(newApplications)
            );
            console.log("Successfully updated localStorage");
          } catch (error) {
            console.error("Error updating localStorage:", error);
          }

          return newApplications;
        });

        // Verify the update
        setTimeout(() => {
          const storedApps = localStorage.getItem("applications");
          console.log(
            "Verification - Applications in localStorage:",
            storedApps
          );
          console.log("Verification - Applications in state:", allApplications);
        }, 100);

        setFormData(null);
        console.log("Form data reset to null");

        setPage("status");
        console.log("Page changed to status");
      } catch (error) {
        console.error("Error in submission process:", error);
      }
    } else {
      console.error("Form submission failed - Missing data:", {
        hasFormData: !!formData,
        currentUser,
        formDataContent: formData,
      });
    }
  };

  const handleSetFormData: FormDataSetter = (data) => {
    console.log("handleSetFormData called with:", data);

    if (typeof data === "function") {
      setFormData((prev) => {
        console.log("Previous form data:", prev);
        const result = data(prev);
        if (result) {
          const newData = { ...defaultFormData, ...result };
          console.log("New form data (function):", newData);
          return newData;
        }
        console.log("Function returned null");
        return null;
      });
    } else {
      const newData = data ? { ...defaultFormData, ...data } : null;
      console.log("New form data (direct):", newData);
      setFormData(newData);
    }
  };

  const handleUpdateStatus = (applicationId: number, newStatus: string) => {
    const updatedApplications = allApplications.map((app, index) => {
      if (index === applicationId) {
        return { ...app, status: newStatus };
      }
      return app;
    });
    setAllApplications(updatedApplications);
  };

  // Lọc hồ sơ của người dùng hiện tại
  const userApplications = allApplications.filter((app) => {
    const matches = app.personalInfo?.name === currentUser;
    console.log("Filtering application:", {
      appName: app.personalInfo?.name,
      currentUser,
      isMatch: matches,
    });
    return matches;
  });

  // Monitor state changes
  useEffect(() => {
    console.log("State changed:");
    console.log("Current user:", currentUser);
    console.log("All applications:", allApplications);
    console.log("User applications:", userApplications);
    console.log("Current page:", page);
  }, [currentUser, allApplications, userApplications, page]);

  // Add cleanup on unmount
  useEffect(() => {
    return () => {
      console.log("Component unmounting - Final state:", {
        currentUser,
        allApplications,
        userApplications,
      });
    };
  }, []);

  const renderContent = () => {
    switch (page) {
      case "dashboard":
        return <Dashboard currentUser={currentUser} setPage={setPage} />;
      case "admin-dashboard":
        return (
          <AdminDashboard
            setPage={setPage}
            currentUser={currentUser}
            applications={allApplications}
            onUpdateStatus={handleUpdateStatus}
          />
        );
      case "apply":
        return (
          <ApplicationForm
            setPage={setPage}
            currentUser={currentUser}
            setFormData={handleSetFormData}
            nextStep={handleNextStep}
          />
        );
      case "apply-transcript":
        return (
          <TranscriptForm
            setPage={setPage}
            currentUser={currentUser}
            setFormData={handleSetFormData}
            nextStep={handleNextStep}
          />
        );
      case "status":
        return (
          <ApplicationStatus
            setPage={setPage}
            currentUser={currentUser}
            applications={isAdmin ? allApplications : userApplications}
          />
        );
      case "results":
        const latestApplication = userApplications[userApplications.length - 1];
        return (
          <ExamResults
            setPage={setPage}
            currentUser={currentUser}
            applicationData={latestApplication}
          />
        );
      case "guide":
        return (
          <GuideRegistration setPage={setPage} currentUser={currentUser} />
        );
      default:
        return <Dashboard currentUser={currentUser} setPage={setPage} />;
    }
  };

  return (
    <UniversityProvider>
      {currentUser === null ? (
        page === "register" ? (
          <Register setCurrentUser={setCurrentUser} setPage={setPage} />
        ) : (
          <Login
            setCurrentUser={setCurrentUser}
            setPage={setPage}
            setIsAdmin={setIsAdmin}
          />
        )
      ) : (
        renderContent()
      )}
    </UniversityProvider>
  );
};

export default App;
