import React from 'react';
import { Button, Card, Table, Tag, Typography } from 'antd';
import type { ColumnsType } from 'antd/es/table';
import '../../styles.css';

// Define possible page types for type safety
type Page = 'dashboard' | 'status' | 'results' | 'apply';

interface ResultData {
  key: string;
  university: string;
  major: string;
  admissionMethod: string;
  status: 'pending' | 'approved' | 'rejected';
  score: number;
  note?: string;
}

interface ResultsProps {
  onNavigate: (page: Page) => void;
}

const Results: React.FC<ResultsProps> = ({ onNavigate }) => {
  // Mock data - replace with actual API call
  const resultsData: ResultData[] = [
    {
      key: '1',
      university: 'Đại học Bách Khoa Hà Nội',
      major: 'Công nghệ Thông tin',
      admissionMethod: 'Xét điểm thi THPT',
      status: 'approved',
      score: 28.5,
      note: 'Nhập học trước 15/9'
    },
    {
      key: '2',
      university: 'Đại học Kinh tế Quốc dân',
      major: 'Quản trị Kinh doanh',
      admissionMethod: 'Xét học bạ',
      status: 'pending',
      score: 26.8
    },
    {
      key: '3',
      university: 'Đại học Ngoại thương',
      major: 'Kinh tế Đối ngoại',
      admissionMethod: 'Xét điểm thi THPT',
      status: 'rejected',
      score: 25.2,
      note: 'Không đủ điểm chuẩn'
    }
  ];

  const columns: ColumnsType<ResultData> = [
    {
      title: 'Trường Đại học',
      dataIndex: 'university',
      key: 'university',
    },
    {
      title: 'Ngành học',
      dataIndex: 'major',
      key: 'major',
    },
    {
      title: 'Phương thức',
      dataIndex: 'admissionMethod',
      key: 'admissionMethod',
    },
    {
      title: 'Điểm',
      dataIndex: 'score',
      key: 'score',
      align: 'center',
      render: (score: number) => <strong>{score}</strong>,
    },
    {
      title: 'Trạng thái',
      dataIndex: 'status',
      key: 'status',
      render: (status: ResultData['status']) => {
        const statusMap = {
          approved: { color: 'green', text: 'Trúng tuyển' },
          pending: { color: 'orange', text: 'Chờ xét' },
          rejected: { color: 'red', text: 'Không trúng tuyển' },
        };
        return <Tag color={statusMap[status].color}>{statusMap[status].text}</Tag>;
      },
    },
    {
      title: 'Ghi chú',
      dataIndex: 'note',
      key: 'note',
      render: (note?: string) => note || '-',
    },
  ];

  return (
    <div className="results-container">
      <Typography.Title level={2} className="results-title">
        Kết quả xét tuyển đại học
      </Typography.Title>
      
      <Card className="results-card">
        <Table
          columns={columns}
          dataSource={resultsData}
          pagination={false}
          bordered
          summary={() => (
            <Table.Summary fixed>
              <Table.Summary.Row>
                <Table.Summary.Cell index={0} colSpan={6}>
                  <div className="results-summary">
                    <span>Kết quả chính thức sẽ được cập nhật theo thông báo của Bộ GD&ĐT</span>
                    <Button 
                      type="primary" 
                      onClick={() => onNavigate('dashboard')}
                      className="back-button"
                    >
                      Quay lại trang chủ
                    </Button>
                  </div>
                </Table.Summary.Cell>
              </Table.Summary.Row>
            </Table.Summary>
          )}
        />
      </Card>

      <div className="results-footer">
        <p>Mọi thắc mắc vui lòng liên hệ phòng tuyển sinh của trường</p>
        <p>Hotline hỗ trợ: 1800 1234</p>
      </div>
    </div>
  );
};

export default Results;
