// src/components/application/CombinedForm.tsx
import React, { useState } from 'react';
import { FormData } from '../../types';
import './CombinedForm.css';

interface CombinedFormProps {
  setFormData: React.Dispatch<React.SetStateAction<FormData>>;
  nextStep: () => void;
}

const CombinedForm: React.FC<CombinedFormProps> = ({ setFormData, nextStep }) => {
  const [localData, setLocalData] = useState<FormData>({
    school: '',
    major: '',
    examCombination: '',
    personalInfo: { name: '', dateOfBirth: '', address: '', phone: '', cccd: '', ethnicity: '', gender: '' },
    scores: {},
    priorityCategories: [],
    documents: []
  });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setLocalData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePersonalInfoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setLocalData((prev) => ({
      ...prev,
      personalInfo: { ...prev.personalInfo, [name]: value }
    }));
  };

  const handleScoreChange = (subject: string, value: string) => {
    setLocalData((prev) => ({
      ...prev,
      scores: { ...prev.scores, [subject]: parseFloat(value) }
    }));
  };

  const handlePriorityChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setLocalData((prev) => ({
      ...prev,
      priorityCategories: [e.target.value]
    }));
  };

  const handleGenderChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { value } = e.target;
    setLocalData((prev) => ({
      ...prev,
      personalInfo: { ...prev.personalInfo, gender: value }
    }));
  };

  const handleSubmit = () => {
    setFormData(localData);
    nextStep();
  };

  return (
    <div className="combined-form">
      <h2>ÄÄng kÃ½ xÃ©t tuyá»n</h2>

      {/* ThÃ´ng tin cÃ¡ nhÃ¢n */}
      <div className="form-section">
        <h3>A. ThÃ´ng tin cÃ¡ nhÃ¢n</h3>
        <input
          type="text"
          name="name"
          placeholder="Há» tÃªn"
          value={localData.personalInfo.name}
          onChange={handlePersonalInfoChange}
        />
        <input
          type="date"
          name="dateOfBirth"
          value={localData.personalInfo.dateOfBirth}
          onChange={handlePersonalInfoChange}
        />
        <input
          type="text"
          name="address"
          placeholder="Äá»a chá»"
          value={localData.personalInfo.address}
          onChange={handlePersonalInfoChange}
        />
        <input
          type="tel"
          name="phone"
          placeholder="Sá» Äiá»n thoáº¡i"
          value={localData.personalInfo.phone}
          onChange={handlePersonalInfoChange}
        />
        <input
          type="text"
          name="cccd"
          placeholder="Sá» CCCD"
          value={localData.personalInfo.cccd}
          onChange={handlePersonalInfoChange}
        />
        <input
          type="text"
          name="ethnicity"
          placeholder="DÃ¢n tá»c"
          value={localData.personalInfo.ethnicity}
          onChange={handlePersonalInfoChange}
        />
        <div className="gender-section">
          <label>Giá»i tÃ­nh:</label>
          <div>
            <input
              type="radio"
              id="male"
              name="gender"
              value="Nam"
              checked={localData.personalInfo.gender === 'Nam'}
              onChange={handleGenderChange}
            />
            <label htmlFor="male">Nam</label>
          </div>
          <div>
            <input
              type="radio"
              id="female"
              name="gender"
              value="Ná»¯"
              checked={localData.personalInfo.gender === 'Ná»¯'}
              onChange={handleGenderChange}
            />
            <label htmlFor="female">Ná»¯</label>
          </div>
        </div>
      </div>

      {/* Chá»n trÆ°á»ng, ngÃ nh, tá» há»£p */}
      <div className="form-section">
        <h3>B. Chá»n trÆ°á»ng vÃ  ngÃ nh</h3>
        <select name="school" value={localData.school} onChange={handleInputChange}>
          <option value="">Chá»n trÆ°á»ng</option>
          <option value="TrÆ°á»ng A">TrÆ°á»ng A</option>
          <option value="TrÆ°á»ng B">TrÆ°á»ng B</option>
          <option value="TrÆ°á»ng C">TrÆ°á»ng C</option>
        </select>
        <select name="major" value={localData.major} onChange={handleInputChange}>
          <option value="">Chá»n ngÃ nh</option>
          <option value="NgÃ nh X">NgÃ nh X</option>
          <option value="NgÃ nh Y">NgÃ nh Y</option>
          <option value="NgÃ nh Z">NgÃ nh Z</option>
        </select>
        <select name="examCombination" value={localData.examCombination} onChange={handleInputChange}>
          <option value="">Chá»n tá» há»£p</option>
          <option value="A00">A00</option>
          <option value="A01">A01</option>
          <option value="D01">D01</option>
        </select>
      </div>

      {/* Äiá»m thi */}
      <div className="form-section">
        <h3>C. Äiá»m thi</h3>
        <input
          type="number"
          placeholder="Äiá»m mÃ´n 1"
          onChange={(e) => handleScoreChange('subject1', e.target.value)}
        />
        <input
          type="number"
          placeholder="Äiá»m mÃ´n 2"
          onChange={(e) => handleScoreChange('subject2', e.target.value)}
        />
        <input
          type="number"
          placeholder="Äiá»m mÃ´n 3"
          onChange={(e) => handleScoreChange('subject3', e.target.value)}
        />
      </div>

      {/* Äá»i tÆ°á»£ng Æ°u tiÃªn */}
      <div className="form-section">
        <h3>D. Äá»i tÆ°á»£ng Æ°u tiÃªn</h3>
        <select onChange={handlePriorityChange}>
          <option value="">Chá»n Äá»i tÆ°á»£ng</option>
          <option value="Æ¯u tiÃªn 1">Æ¯u tiÃªn 1</option>
          <option value="Æ¯u tiÃªn 2">Æ¯u tiÃªn 2</option>
          <option value="KhÃ´ng Æ°u tiÃªn">KhÃ´ng Æ°u tiÃªn</option>
        </select>
      </div>

      <button className="next-button" onClick={handleSubmit}>
        Tiáº¿p theo
      </button>
    </div>
  );
};

export default CombinedForm;
