//cho phép người dùng nhập thông tin cá nhân.
import React from 'react';
import { FormData } from '../../types';
import '../../styles.css';

interface Step2Props {
  formData: FormData;
  updateFormData: (data: Partial<FormData>) => void;
  nextStep: () => void;
  prevStep: () => void;
}

const Step2_PersonalInfo: React.FC<Step2Props> = ({ formData, updateFormData, nextStep, prevStep }) => (
  <div className="form-step">
    <h2>Thông tin cá nhân</h2>
    <div className="form-group">
      <label>Họ tên</label>
      <input
        type="text"
        value={formData.personalInfo.name}
        onChange={(e) => updateFormData({ personalInfo: { ...formData.personalInfo, name: e.target.value } })}
        placeholder="Nhập họ tên"
      />
    </div>
    <div className="form-group">
      <label>Ngày sinh</label>
      <input
        type="date"
        value={formData.personalInfo.dateOfBirth}
        onChange={(e) => updateFormData({ personalInfo: { ...formData.personalInfo, dateOfBirth: e.target.value } })}
      />
    </div>
    <div className="form-group">
      <label>Địa chỉ</label>
      <input
        type="text"
        value={formData.personalInfo.address}
        onChange={(e) => updateFormData({ personalInfo: { ...formData.personalInfo, address: e.target.value } })}
        placeholder="Nhập địa chỉ"
      />
    </div>
    <div className="form-navigation">
      <button className="prev-button" onClick={prevStep}>Quay lại</button>
      <button className="next-button" onClick={nextStep}>Tiếp theo</button>
    </div>
  </div>
);

export default Step2_PersonalInfo;
