// cho phép người dùng tải lên các tài liệu minh chứng.
import React from 'react';
import { FormData } from '../../types';
import '../../styles.css';

interface Step4Props {
  formData: FormData;
  updateFormData: (data: Partial<FormData>) => void;
  nextStep: () => void;
  prevStep: () => void;
}

const Step4_UploadDocuments: React.FC<Step4Props> = ({ formData, updateFormData, nextStep, prevStep }) => {
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      updateFormData({ documents: Array.from(e.target.files) });
    }
  };

  return (
    <div className="form-step">
      <h2>Tải lên minh chứng</h2>
      <div className="form-group">
        <label>Chọn tệp (PDF, JPEG, PNG)</label>
        <input
          type="file"
          accept=".pdf,.jpeg,.jpg,.png"
          multiple
          onChange={handleFileChange}
        />
      </div>
      <ul>
        {formData.documents.map((file, index) => (
          <li key={index}>{file.name}</li>
        ))}
      </ul>
      <div className="form-navigation">
        <button className="prev-button" onClick={prevStep}>Quay lại</button>
        <button className="next-button" onClick={nextStep}>Tiếp theo</button>
      </div>
    </div>
  );
};

export default Step4_UploadDocuments;
