// App.tsx
import React, { useState } from 'react';
import Login from './components/auth/Login';
import Register from './components/auth/Register';
import Dashboard from './pages/Dashboard';
import ApplicationForm from './components/application/ApplicationForm';
import ApplicationStatus from './components/application/ApplicationStatus';
import Results from './components/application/Results';
import './styles.css';

type Page = 
  | 'login' 
  | 'register' 
  | 'dashboard' 
  | 'apply' 
  | 'status' 
  | 'results' 
  | 'logout';

const App: React.FC = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState<string | undefined>(undefined);
  const [currentPage, setCurrentPage] = useState<Page>('dashboard');

  const handleLogin = (username: string) => {
    setLoggedIn(true);
    setCurrentUser(username);
    setCurrentPage('dashboard');
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setCurrentUser(undefined);
    setCurrentPage('dashboard');
  };

  const handleNavigate = (page: Page) => {
    setCurrentPage(page);
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'login':
        return <Login 
                onLogin={handleLogin} 
                onNavigate={() => handleNavigate('register')} 
              />;
      
      case 'register':
        return <Register 
                onRegister={handleLogin} 
                onNavigate={() => handleNavigate('login')} 
              />;
      
      case 'dashboard':
        return <Dashboard 
                currentUser={currentUser} 
                onNavigate={handleNavigate} 
                onLogout={handleLogout}
              />;
      
      case 'apply':
        return loggedIn 
          ? <ApplicationForm onNavigate={handleNavigate} /> 
          : null;
      
      case 'status':
        return loggedIn 
          ? <ApplicationStatus onNavigate={handleNavigate} /> 
          : null;
      
      case 'results':
        return loggedIn 
          ? <Results onNavigate={handleNavigate} /> 
          : null;
      
      case 'logout':
        handleLogout();
        return null;
      
      default:
        return <Dashboard 
                currentUser={currentUser} 
                onNavigate={handleNavigate} 
                onLogout={handleLogout}
              />;
    }
  };

  // Redirect to dashboard if trying to access protected pages while not logged in
  if (!loggedIn && ['apply', 'status', 'results'].includes(currentPage)) {
    handleNavigate('dashboard');
    return null;
  }

  return renderPage();
};

export default App;
