import React from "react";
import "../../styles.css";
import { FormData } from "../../types";
import CommonLayout from "./CommonLayout";

interface ApplicationWithStatus extends FormData {
  status: "Chờ duyệt" | "Đã duyệt" | "Từ chối";
  submissionDate: string;
}

interface ApplicationStatusProps {
  setPage: (page: string) => void;
  currentUser?: string | null | undefined;
  applications: FormData[];
}

const ApplicationStatus: React.FC<ApplicationStatusProps> = ({
  setPage,
  currentUser,
  applications,
}) => {
  // Convert FormData to ApplicationWithStatus
  const applicationsWithStatus: ApplicationWithStatus[] = applications.map(
    (app) => ({
      ...app,
      status: "Chờ duyệt",
      submissionDate: new Date().toLocaleDateString(),
    })
  );

  return (
    <CommonLayout
      setPage={setPage}
      currentUser={currentUser}
      activePage="status"
    >
      <div className="status-container">
        <h2>Trạng thái hồ sơ</h2>
        {applicationsWithStatus.length === 0 ? (
          <div className="no-applications">
            <p>Bạn chưa có hồ sơ xét tuyển nào.</p>
            <button onClick={() => setPage("apply")} className="action-button">
              Đăng ký xét tuyển
            </button>
          </div>
        ) : (
          <div className="applications-list">
            {applicationsWithStatus.map((application, index) => (
              <div key={index} className="application-card">
                <div className="application-header">
                  <h3>{application.school}</h3>
                  <span
                    className={`status-badge status-${application.status.toLowerCase()}`}
                  >
                    {application.status}
                  </span>
                </div>
                <div className="detail-section">
                  <h4>Thông tin cá nhân</h4>
                  <p>Họ tên: {application.personalInfo.name}</p>
                  <p>CCCD: {application.personalInfo.cccd}</p>
                </div>
                <div className="detail-section">
                  <h4>Thông tin đăng ký</h4>
                  <p>Ngành: {application.major}</p>
                  {application.examCombination && (
                    <p>Tổ hợp xét tuyển: {application.examCombination}</p>
                  )}
                </div>
                <div className="submission-info">
                  Ngày nộp: {application.submissionDate}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </CommonLayout>
  );
};

export default ApplicationStatus;
