// cho phép người dùng chọn trường, ngành và tổ hợp xét tuyển
import React from 'react';
import { FormData } from './types';
import '../../styles.css';

interface Step1Props {
  formData: FormData;
  updateFormData: (data: Partial<FormData>) => void;
  nextStep: () => void;
}

const Step1_SelectOptions: React.FC<Step1Props> = ({ formData, updateFormData, nextStep }) => (
  <div className="form-step">
    <h2>Chọn trường, ngành, tổ hợp xét tuyển</h2>
    <div className="form-group">
      <label>Trường</label>
      <select value={formData.school} onChange={(e) => updateFormData({ school: e.target.value })}>
        <option value="">Chọn trường</option>
        <option value="Trường A">Trường A</option>
        <option value="Trường B">Trường B</option>
        <option value="Trường C">Trường C</option>
      </select>
    </div>
    <div className="form-group">
      <label>Ngành</label>
      <select value={formData.major} onChange={(e) => updateFormData({ major: e.target.value })}>
        <option value="">Chọn ngành</option>
        <option value="Ngành X">Ngành X</option>
        <option value="Ngành Y">Ngành Y</option>
        <option value="Ngành Z">Ngành Z</option>
      </select>
    </div>
    <div className="form-group">
      <label>Tổ hợp xét tuyển</label>
      <select value={formData.examCombination} onChange={(e) => updateFormData({ examCombination: e.target.value })}>
        <option value="">Chọn tổ hợp</option>
        <option value="A00">A00</option>
        <option value="A01">A01</option>
        <option value="D01">D01</option>
      </select>
    </div>
    <button className="next-button" onClick={nextStep}>Tiếp theo</button>
  </div>
);

export default Step1_SelectOptions;
