import React, { useState, useEffect } from 'react';
import { notification } from 'antd';
import './auth.css';

interface LoginProps {
  setLoggedIn: (value: boolean) => void;
  setPage: (page: string) => void;
}

const Login: React.FC<LoginProps> = ({ setLoggedIn, setPage }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    // Hiện thông báo khi component mount
    notification.info({
      message: 'Thông báo',
      description: 'Hệ thống đang bảo trì từ 19h00 đến 22h00 ngày 16/05',
      placement: 'topRight',
      duration: 5,
    });
  }, []);

  const handleLogin = () => {
    if (username && password) {
      setLoggedIn(true);
      setPage('dashboard');
    } else {
      notification.warning({
        message: 'Lỗi đăng nhập',
        description: 'Vui lòng nhập đầy đủ tài khoản và mật khẩu',
        placement: 'topRight',
        duration: 3,
      });
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-content">
        <div className="login-container">
          <h2>Đăng nhập</h2>
          <div className="form-group">
            <label>
              Số CCCD/CMND/ĐDCN <span className="required">*</span>
            </label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Nhập số CCCD/CMND/ĐDCN"
            />
          </div>
          <div className="form-group">
            <label>
              Mật khẩu <span className="required">*</span>
            </label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Nhập mật khẩu"
            />
          </div>
          <button className="login-button" onClick={handleLogin}>
            Đăng nhập
          </button>
          <p className="register-link">
            Chưa có tài khoản?{' '}
            <a onClick={() => setPage('register')}>Đăng ký ngay</a>
          </p>
        </div>
        <div className="notification">
          <h2>Thông báo</h2>
          <p>Hệ thống đang tiến hành bảo trì, thời gian bảo trì từ 19h00 đến 22h00 ngày 16/05</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
