//phần chính quản lý các bước của biểu mẫu.
import React, { useState } from 'react';
import { FormData } from '../../types';
import Step1_SelectOptions from './Step1_SelectOptions';
import Step2_PersonalInfo from './Step2_PersonalInfo';
import Step3_ScoresAndPriorities from './Step3_ScoresAndPriorities';
import Step4_UploadDocuments from './Step4_UploadDocuments';
import Step5_ReviewAndSubmit from './Step5_ReviewAndSubmit';
import '../../styles.css';

interface ApplicationFormProps {
  setPage: (page: string) => void;
}

const ApplicationForm: React.FC<ApplicationFormProps> = ({ setPage }) => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState<FormData>({
    school: '',
    major: '',
    examCombination: '',
    personalInfo: { name: '', dateOfBirth: '', address: '' },
    scores: {},
    priorityCategories: [],
    documents: []
  });

  const nextStep = () => setStep(step + 1);
  const prevStep = () => setStep(step - 1);
  const updateFormData = (data: Partial<FormData>) => setFormData({ ...formData, ...data });
  const submitForm = () => {
    console.log('Hồ sơ đã gửi:', formData);
    setPage('status');
  };

  switch (step) {
    case 1:
      return <Step1_SelectOptions formData={formData} updateFormData={updateFormData} nextStep={nextStep} />;
    case 2:
      return <Step2_PersonalInfo formData={formData} updateFormData={updateFormData} nextStep={nextStep} prevStep={prevStep} />;
    case 3:
      return <Step3_ScoresAndPriorities formData={formData} updateFormData={updateFormData} nextStep={nextStep} prevStep={prevStep} />;
    case 4:
      return <Step4_UploadDocuments formData={formData} updateFormData={updateFormData} nextStep={nextStep} prevStep={prevStep} />;
    case 5:
      return <Step5_ReviewAndSubmit formData={formData} prevStep={prevStep} submitForm={submitForm} />;
    default:
      return null;
  }
};

export default ApplicationForm;
