import React, { useState, useEffect } from "react";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import Dashboard from "./pages/Dashboard";
import ApplicationForm from "./components/application/ApplicationForm";
import ApplicationStatus from "./components/application/ApplicationStatus";
import Results from "./pages/Results";
import ExamResults from "./pages/ExamResults";
import GuideRegistration from "./components/application/GuideRegistration";
import TranscriptForm from "./components/application/TranscriptForm";
import AdminDashboard from "./pages/AdminDashboard";
import { UniversityProvider } from "./contexts/UniversityContext";
import { FormData, FormDataSetter } from "./types";
import "./styles.css";

const defaultFormData: FormData = {
  school: "",
  major: "",
  examCombination: "",
  personalInfo: {
    name: "",
    dateOfBirth: "",
    address: "",
    phone: "",
    cccd: "",
    ethnicity: "",
    gender: "",
  },
  scores: {},
  transcriptScores: {
    semester1Grade12: {
      math: "",
      literature: "",
      english: "",
      physics: "",
      chemistry: "",
      biology: "",
    },
    semester2Grade12: {
      math: "",
      literature: "",
      english: "",
      physics: "",
      chemistry: "",
      biology: "",
    },
    semester1Grade11: {
      math: "",
      literature: "",
      english: "",
      physics: "",
      chemistry: "",
      biology: "",
    },
    semester2Grade11: {
      math: "",
      literature: "",
      english: "",
      physics: "",
      chemistry: "",
      biology: "",
    },
  },
  priorityCategories: [],
  documents: [],
  documents2: [],
  documents3: [],
  status: "Chờ duyệt",
  submissionDate: new Date().toLocaleDateString(),
};

const App: React.FC = () => {
  const [currentUser, setCurrentUser] = useState<string | null>(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [page, setPage] = useState("dashboard");
  const [formData, setFormData] = useState<FormData | null>(null);
  const [allApplications, setAllApplications] = useState<FormData[]>([]);

  // Load initial data
  useEffect(() => {
    const user = localStorage.getItem("currentUser");
    if (user) {
      setCurrentUser(user);
      setIsAdmin(user === "admin");
    }

    const savedApplications = localStorage.getItem("applications");
    if (savedApplications) {
      try {
        const parsed = JSON.parse(savedApplications);
        setAllApplications(parsed);
        console.log("Loaded applications from storage:", parsed);
      } catch (error) {
        console.error("Error parsing saved applications:", error);
        setAllApplications([]);
      }
    }
  }, []);

  // Handle logout
  useEffect(() => {
    if (page === "logout") {
      setCurrentUser(null);
      setIsAdmin(false);
      localStorage.removeItem("currentUser");
      setPage("login");
    }
  }, [page]);

  const saveApplications = (applications: FormData[]) => {
    try {
      localStorage.setItem("applications", JSON.stringify(applications));
      console.log("Saved applications to storage:", applications);
    } catch (error) {
      console.error("Error saving applications:", error);
    }
  };

  const handleNextStep = () => {
    if (!formData || !currentUser) {
      console.error("Missing form data or user:", { formData, currentUser });
      return;
    }

    const newApplication = {
      ...formData,
      status: "Chờ duyệt",
      submissionDate: new Date().toLocaleDateString(),
      personalInfo: {
        ...formData.personalInfo,
        name: currentUser,
      },
    };

    const newApplications = [...allApplications, newApplication];

    // Update state and storage together
    setAllApplications(newApplications);
    saveApplications(newApplications);

    // Reset form and change page
    setFormData(null);
    setPage("status");

    console.log("Application submitted:", {
      newApplication,
      allApplications: newApplications,
    });
  };

  const handleSetFormData: FormDataSetter = (data) => {
    if (typeof data === "function") {
      setFormData((prev) => {
        const result = data(prev);
        return result ? { ...defaultFormData, ...result } : null;
      });
    } else {
      setFormData(data ? { ...defaultFormData, ...data } : null);
    }
  };

  const handleUpdateStatus = (applicationId: number, newStatus: string) => {
    const updatedApplications = allApplications.map((app, index) =>
      index === applicationId ? { ...app, status: newStatus } : app
    );
    setAllApplications(updatedApplications);
    saveApplications(updatedApplications);
  };

  // Filter applications for current user
  const userApplications = allApplications.filter(
    (app) => app.personalInfo?.name === currentUser
  );

  const renderContent = () => {
    switch (page) {
      case "dashboard":
        return <Dashboard currentUser={currentUser} setPage={setPage} />;
      case "admin-dashboard":
        return (
          <AdminDashboard
            setPage={setPage}
            currentUser={currentUser}
            applications={allApplications}
            onUpdateStatus={handleUpdateStatus}
          />
        );
      case "apply":
        return (
          <ApplicationForm
            setPage={setPage}
            currentUser={currentUser}
            setFormData={handleSetFormData}
            nextStep={handleNextStep}
          />
        );
      case "apply-transcript":
        return (
          <TranscriptForm
            setPage={setPage}
            currentUser={currentUser}
            setFormData={handleSetFormData}
            nextStep={handleNextStep}
          />
        );
      case "status":
        return (
          <ApplicationStatus
            setPage={setPage}
            currentUser={currentUser}
            applications={isAdmin ? allApplications : userApplications}
          />
        );
      case "results":
        return (
          <ExamResults
            setPage={setPage}
            currentUser={currentUser}
            applicationData={userApplications[userApplications.length - 1]}
          />
        );
      case "guide":
        return (
          <GuideRegistration setPage={setPage} currentUser={currentUser} />
        );
      default:
        return <Dashboard currentUser={currentUser} setPage={setPage} />;
    }
  };

  return (
    <UniversityProvider>
      {currentUser === null ? (
        page === "register" ? (
          <Register setCurrentUser={setCurrentUser} setPage={setPage} />
        ) : (
          <Login
            setCurrentUser={setCurrentUser}
            setPage={setPage}
            setIsAdmin={setIsAdmin}
          />
        )
      ) : (
        renderContent()
      )}
    </UniversityProvider>
  );
};

export default App;
