// cho phép người dùng nhập điểm thi và chọn đối tượng ưu tiên.
import React from 'react';
import { FormData } from './types';
import '../../styles.css';

interface Step3Props {
  formData: FormData;
  updateFormData: (data: Partial<FormData>) => void;
  nextStep: () => void;
  prevStep: () => void;
}

const Step3_ScoresAndPriorities: React.FC<Step3Props> = ({ formData, updateFormData, nextStep, prevStep }) => (
  <div className="form-step">
    <h2>Điểm thi và đối tượng ưu tiên</h2>
    <div className="form-group">
      <label>Điểm môn 1</label>
      <input
        type="number"
        value={formData.scores.subject1 || ''}
        onChange={(e) => updateFormData({ scores: { ...formData.scores, subject1: parseFloat(e.target.value) } })}
        placeholder="Nhập điểm môn 1"
      />
    </div>
    <div className="form-group">
      <label>Đối tượng ưu tiên</label>
      <select
        value={formData.priorityCategories[0] || ''}
        onChange={(e) => updateFormData({ priorityCategories: [e.target.value] })}
      >
        <option value="">Chọn đối tượng</option>
        <option value="Ưu tiên 1">Ưu tiên 1</option>
        <option value="Ưu tiên 2">Ưu tiên 2</option>
        <option value="Không ưu tiên">Không ưu tiên</option>
      </select>
    </div>
    <div className="form-navigation">
      <button className="prev-button" onClick={prevStep}>Quay lại</button>
      <button className="next-button" onClick={nextStep}>Tiếp theo</button>
    </div>
  </div>
);

export default Step3_ScoresAndPriorities;
