import React, { useState } from 'react';
import { FormData } from '../../types';
import './CombinedForm.css';

interface CombinedFormProps {
  setFormData: React.Dispatch<React.SetStateAction<FormData>>;
  nextStep: () => void;
}

const CombinedForm: React.FC<CombinedFormProps> = ({ setFormData, nextStep }) => {
  const [localData, setLocalData] = useState<FormData>({
    school: '',
    major: '',
    examCombination: '',
    personalInfo: { name: '', dateOfBirth: '', address: '', phone: '', cccd: '', ethnicity: '', gender: '' },
    scores: {},
    priorityCategories: [],
    documents: []
  });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setLocalData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePersonalInfoChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    let updatedValue = value;
    // Loại bỏ ký tự không phải số cho CCCD và phone
    if (name === 'cccd' || name === 'phone') {
      updatedValue = value.replace(/\D/g, '');
    }
    setLocalData((prev) => ({
      ...prev,
      personalInfo: { ...prev.personalInfo, [name]: updatedValue }
    }));
  };

  const handleScoreChange = (subject: string, value: string) => {
    setLocalData((prev) => ({
      ...prev,
      scores: { ...prev.scores, [subject]: parseFloat(value) }
    }));
  };

  const handlePriorityChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    setLocalData((prev) => ({
      ...prev,
      priorityCategories: [e.target.value]
    }));
  };

  const handlePaste = (e: React.ClipboardEvent<HTMLInputElement>) => {
    const pastedText = e.clipboardData.getData('text');
    if (!/^\d*$/.test(pastedText)) {
      e.preventDefault(); // Ngăn dán nếu không phải số
    }
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (e.currentTarget.checkValidity()) {
      setFormData(localData);
      nextStep();
    }
  };

  return (
    <form className="combined-form" onSubmit={handleSubmit}>
      <h2>Đăng ký xét tuyển</h2>

      {/* Thông tin cá nhân */}
      <div className="form-section">
        <h3>A. Thông tin cá nhân</h3>
        <input
          type="text"
          name="name"
          placeholder="Họ tên"
          value={localData.personalInfo.name}
          onChange={handlePersonalInfoChange}
        />
        <select
          name="gender"
          value={localData.personalInfo.gender}
          onChange={handlePersonalInfoChange}
        >
          <option value="">Chọn giới tính</option>
          <option value="Nam">Nam</option>
          <option value="Nữ">Nữ</option>
        </select>
        <input
          type="date"
          name="dateOfBirth"
          value={localData.personalInfo.dateOfBirth}
          onChange={handlePersonalInfoChange}
        />
        <input
          type="text"
          name="address"
          placeholder="Địa chỉ"
          value={localData.personalInfo.address}
          onChange={handlePersonalInfoChange}
        />
        <input
          type="tel"
          name="phone"
          placeholder="Số điện thoại"
          value={localData.personalInfo.phone}
          onChange={handlePersonalInfoChange}
          onPaste={handlePaste}
          pattern="\d{10}"
          title="Vui lòng nhập 10 chữ số"
          maxLength={10}
        />
        <input
          type="text"
          name="cccd"
          placeholder="Số CCCD"
          value={localData.personalInfo.cccd}
          onChange={handlePersonalInfoChange}
          onPaste={handlePaste}
          pattern="\d{12}"
          title="Vui lòng nhập 12 chữ số"
          maxLength={12}
          inputMode="numeric"
        />
        <input
          type="text"
          name="ethnicity"
          placeholder="Dân tộc"
          value={localData.personalInfo.ethnicity}
          onChange={handlePersonalInfoChange}
        />
      </div>

      {/* Chọn trường, ngành, tổ hợp */}
      <div className="form-section">
        <h3>B. Chọn trường và ngành</h3>
        <select name="school" value={localData.school} onChange={handleInputChange}>
          <option value="">Chọn trường</option>
          <option value="Trường A">Trường A</option>
          <option value="Trường B">Trường B</option>
          <option value="Trường C">Trường C</option>
        </select>
        <select name="major" value={localData.major} onChange={handleInputChange}>
          <option value="">Chọn ngành</option>
          <option value="Ngành X">Ngành X</option>
          <option value="Ngành Y">Ngành Y</option>
          <option value="Ngành Z">Ngành Z</option>
        </select>
        <select name="examCombination" value={localData.examCombination} onChange={handleInputChange}>
          <option value="">Chọn tổ hợp</option>
          <option value="A00">A00</option>
          <option value="A01">A01</option>
          <option value="D01">D01</option>
        </select>
      </div>

      {/* Điểm thi */}
      <div className="form-section">
        <h3>C. Điểm thi</h3>
        <input
          type="number"
          placeholder="Điểm môn 1"
          onChange={(e) => handleScoreChange('subject1', e.target.value)}
        />
        <input
          type="number"
          placeholder="Điểm môn 2"
          onChange={(e) => handleScoreChange('subject2', e.target.value)}
        />
        <input
          type="number"
          placeholder="Điểm môn 3"
          onChange={(e) => handleScoreChange('subject3', e.target.value)}
        />
      </div>

      {/* Đối tượng ưu tiên */}
      <div className="form-section">
        <h3>D. Đối tượng ưu tiên</h3>
        <select onChange={handlePriorityChange}>
          <option value="">Chọn đối tượng</option>
          <option value="Ưu tiên 1">Ưu tiên 1</option>
          <option value="Ưu tiên 2">Ưu tiên 2</option>
          <option value="Không ưu tiên">Không ưu tiên</option>
        </select>
      </div>

      <button type="submit" className="next-button">
        Tiếp theo
      </button>
    </form>
  );
};

export default CombinedForm;
