// src/components/application/ApplicationForm.tsx
import React, { useState } from 'react';
import { FormData } from '../../types';
import CombinedForm from './CombinedForm';
import Step4_UploadDocuments from './Step4_UploadDocuments';
import Step5_ReviewAndSubmit from './Step5_ReviewAndSubmit';
import '../../styles.css';

interface ApplicationFormProps {
  setPage: (page: string) => void;
}

const ApplicationForm: React.FC<ApplicationFormProps> = ({ setPage }) => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState<FormData>({
    school: '',
    major: '',
    examCombination: '',
    personalInfo: { name: '', dateOfBirth: '', address: '' },
    scores: {},
    priorityCategories: [],
    documents: []
  });

  const nextStep = () => setStep(step + 1);
  const prevStep = () => setStep(step - 1);

  const handleFormSubmit = () => {
    console.log('Hồ sơ đã gửi:', formData);
    setPage('status');
  };

  switch (step) {
    case 1:
      return <CombinedForm setFormData={setFormData} nextStep={nextStep} />;
    case 2:
      return <Step4_UploadDocuments formData={formData} setFormData={setFormData} nextStep={nextStep} prevStep={prevStep} />;
    case 3:
      return <Step5_ReviewAndSubmit formData={formData} prevStep={prevStep} submitForm={handleFormSubmit} />;
    default:
      return null;
  }
};

export default ApplicationForm;
